<?phpuse Nette\Application\AppForm;use Nette\Application\JsonResponse;class HomepagePresenter extends BasePresenter{	/**	 * @return Nette\Application\AppForm	 */	protected function createComponentLocationsForm()	{		$form = new AppForm;				$form->addText('from', 'From')			->setRequired('Fill the "from" location, please.');				$form->addText('to', 'To')			->setRequired('Fill the "to" location, please.');				$form->addSubmit('okFindDirections', 'Find directions');		$form->addSubmit('okSubmit', 'Save trip');		$form->onSubmit[] = function() {					};				return $form;	}		/**	 * AJAX signal handler for getting user location string.	 */	public function handleLocation()	{		$latitude = $this->request->post['latitude'];		$longitude = $this->request->post['longitude'];		$service = new LocationService;		$location = $service->getLocation($latitude, $longitude);		$this->terminate(new JsonResponse(array(			'location' => $location->street . ', ' . $location->city . ', ' . $location->country,		)));	}		/**	 * AJAX singla handler for getting navigation directions info.	 */	 		public function handleDirections()	{		$from = $this->request->post['from'];		$to = $this->request->post['to'];				$service = new DirectionsService;		$this->terminate(new JsonResponse($service->getDirections($from, $to)));	}	public function renderDefault()	{		$this->template->locationsForm = $this['locationsForm'];	}}