<?phpuse Nette\Application\AppForm;use Nette\Application\JsonResponse;/** * Presenter for planning a new trip. */class HomepagePresenter extends BasePresenter{	/**	 * @return Nette\Application\AppForm	 */	protected function createComponentLocationsForm()	{		$form = new AppForm;				$form->addText('from', 'From')			->setRequired('Fill the "from" location, please.');				$form->addText('to', 'To')			->setRequired('Fill the "to" location, please.');				$form->addSubmit('okFindDirections', 'Find directions');		$form->addSubmit('okSubmit', 'Save trip');		$form->onSubmit[] = function() {					};				return $form;	}		/**	 * AJAX signal handler for getting user location string.	 */	public function handleLocation()	{		$latitude = $this->request->post['latitude'];		$longitude = $this->request->post['longitude'];		$service = new LocationService;				try {			$location = $service->getLocation($latitude, $longitude);		} catch (InvalidStateException $e) {			$this->terminate(new JsonResponse(array(				'status' => 'FAIL',			)));		}		$this->terminate(new JsonResponse(array(			'status' => 'OK',			'location' => $location->street . ', ' . $location->city . ', ' . $location->country,		)));	}		/**	 * AJAX signal handler for getting navigation directions info.	 */	 		public function handleDirections()	{		$from = $this->request->post['from'];		$to = $this->request->post['to'];				$service = new TripService;				try {			$trip = $service->buildTrip($from, $to);		} catch (InvalidStateException $e) {			$this->terminate(new JsonResponse(array('status' => 'FAIL')));		}				$steps = array();		foreach($trip->steps as $step) {			$arr = array();			$arr['distance'] = $step->distance;			$arr['instructions'] = $step->instructions;			$steps[] = $arr;		}				$this->terminate(new JsonResponse(array(			'status' => 'OK',			'duration' => $trip->duration,			'distance' => $trip->distance,			'steps' => $steps,		)));	}	public function renderDefault()	{		$this->template->locationsForm = $this['locationsForm'];	}}